class Solution:
    def minWindow(self, s: str, t: str) -> str:

        # counter = Counter(t)
        # win_len = len(t)
        # arr = []
        # for index, c in enumerate(s):
        #     if c in t:
        #         arr.append([c, index])
        # start = 0
        # end = 0
        # res = []
        # while end < len(arr):
        #     temp = Counter([i[0] for i in arr[start:end + 1]])
        #
        #     vaild = 0
        #     for c2 in t:
        #         if temp[c2] >= counter[c2]:
        #             vaild += 1
        #         if vaild == win_len:
        #             left = arr[start:end + 1][0][1]
        #             right = arr[start:end + 1][-1][1]
        #             res.append(s[left:right + 1])
        #             end = start
        #             start += 1
        #     end += 1
        # res.sort(key=len)
        # return res[0] if res else ""

        from collections import defaultdict
        findout = defaultdict(int)
        for i in t:
            findout[i] += 1

        min_len, res = float('inf'), ""
        l, counter = len(s), len(t)
        left = right = 0

        while right < l:
            if findout[s[right]] > 0:
                counter -= 1
            findout[s[right]] -= 1  # 说明找到一个元素，right右移
            right += 1
            while counter == 0:  # 找不到元素
                if min_len > right - left:
                    min_len = right - left
                    res = s[left:right]
                if findout[s[left]] == 0:
                    counter += 1
                findout[s[left]] += 1
                left += 1
        return res


if __name__ == '__main__':
    sn = Solution()
    s = "ADOBECODEBANC"
    t = "ABC"

